import React from 'react';

function Question({ question, options, onAnswer }) {
  return (
    <div className="question">
      <p>{question}</p>
      {options.map((option, index) => (
        <button key={index} onClick={() => onAnswer(option)}>
          {option}
        </button>
      ))}
    </div>
  );
}

export default Question;
