import React, { useState } from 'react';
import Question from './Question';
import ImageSelection from './ImageSelection';
import Rating from './Rating';
import Result from './Result';
import { motion, AnimatePresence } from 'framer-motion';
import './Quiz.css';

function Quiz() {
  const [step, setStep] = useState(0);
  const [answers, setAnswers] = useState([]);

  const handleAnswer = (answer) => {
    setAnswers([...answers, answer]);
    setStep(step + 1);
  };

  const questions = [
    { type: 'text', question: 'What genre of movie do you prefer?', options: ['Action', 'Comedy', 'Drama'] },
    { type: 'image', question: 'Select a mood', images: [{ src: 'mood1.jpg', alt: 'Happy' }, { src: 'mood2.jpg', alt: 'Sad' }] },
    { type: 'rating', question: 'Rate this trailer (1-5)', options: [1, 2, 3, 4, 5] },
    { type: 'text', question: 'Which actor do you like the most?', options: ['Tom Hanks', 'Leonardo DiCaprio', 'Meryl Streep'] },
    { type: 'image', question: 'Choose a setting', images: [{ src: 'setting1.jpg', alt: 'City' }, { src: 'setting2.jpg', alt: 'Nature' }] },
    { type: 'rating', question: 'How excited are you for upcoming movies? (1-5)', options: [1, 2, 3, 4, 5] }
  ];

  const currentQuestion = questions[step];

  return (
    <div className="quiz">
      <AnimatePresence>
        {step < questions.length ? (
          <motion.div
            key={step}
            initial={{ opacity: 0, y: 50 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -50 }}
            transition={{ duration: 0.5 }}
          >
            {currentQuestion.type === 'text' && (
              <Question question={currentQuestion.question} options={currentQuestion.options} onAnswer={handleAnswer} />
            )}
            {currentQuestion.type === 'image' && (
              <ImageSelection question={currentQuestion.question} images={currentQuestion.images} onAnswer={handleAnswer} />
            )}
            {currentQuestion.type === 'rating' && (
              <Rating question={currentQuestion.question} onAnswer={handleAnswer} />
            )}
          </motion.div>
        ) : (
          <motion.div
            key="result"
            initial={{ opacity: 0, y: 50 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -50 }}
            transition={{ duration: 0.5 }}
          >
            <Result answers={answers} />
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
}

export default Quiz;
