import React, { useRef } from 'react';
import { Button } from '@mui/material';

function Rating({ question, options, onAnswer, video }) {
  const videoRef = useRef(null);

  const handlePlayVideo = () => {
    videoRef.current.play();
  };

  return (
    <div className="question-container">
      <h2>{question}</h2>
      <div className="video-container">
        <video ref={video} src={video} controls style={{ display: 'block', margin: 'auto', marginBottom: '10px' }} />
      </div>
      <div className="button-container">
        {options.map((option, index) => (
          <Button 
            key={index} 
            variant="contained" 
            color="primary" 
            style={{ margin: '10px', borderRadius: '5px', padding: '30px 45px', fontSize: '32px' }}
            onClick={() => {onAnswer(option); handlePlayVideo();}}
          >
            {option}
          </Button>
        ))}
      </div>
    </div>
  );
}

export default Rating;
