import React, { useState, useEffect } from 'react';
import Question from './Question';
import ImageSelection from './ImageSelection';
import Rating from './Rating';
import Recommendation from './Recommendation';
import FeedbackThanks from './FeedbackThanks';
import { motion, AnimatePresence } from 'framer-motion';
import './Quiz.css';
import {
  happy,
  sad,
  neutral,
  angry,
  la,
  dubai,
  soviet,
  asia,
  cat1,
  cat2,
  cat3,
  cat4,
  donkey,
  ace,
  bean,
  michael,
  fall,
  spring,
  summer,
  winter,
  alone,
  family,
  couple,
  party,
  recommendation,
  barnyard,
  wolf
} from './assets';

function Quiz() {
  const [step, setStep] = useState(0);
  const [answers, setAnswers] = useState([]);
  const [feedback, setFeedback] = useState(null);
  const [recommendations, setRecommendations] = useState(null);

  const handleAnswer = (answer) => {
    if (step === 0) {
      setAnswers(answer); // For the multiple choice question, we set the answer directly
    } else {
      setAnswers([...answers, answer]);
    }
    setStep(step + 1);
  };

  const handleFeedback = (feedback) => {
    setFeedback(feedback);
    if (feedback === 'thumbs-up' && step === questions.length - 1) {
      setStep(step + 1); // Move to FeedbackThanks
    } else if (feedback === 'thumbs-down') {
      setStep(step + 1); // Move to next recommendation
    }
  };

  const questions = [
    {
      type: 'multi-choice',
      question: 'Which streaming platforms do you use? (Select all that apply)',
      options: ['Netflix', 'Disney Plus', 'HBO Max', 'Amazon Prime Video']
    },
    { type: 'image', question: 'Choose your circumstances:', images: [
      { src: alone, alt: 'alone' }, 
      { src: family, alt: 'family' }, 
      { src: couple, alt: 'couple' }, 
      { src: party, alt: 'party' }
    ] },
    { type: 'image', question: 'Which pic describes the best your current mood?', images: [
        { src: happy, alt: 'Happy' }, 
        { src: sad, alt: 'Sad' }, 
        { src: neutral, alt: 'Neutral' }, 
        { src: angry, alt: 'Angry' }
      ] },
    { type: 'image', question: 'Just choose your favourite season:', images: [
        { src: spring, alt: 'spring' }, 
        { src: winter, alt: 'winter' }, 
        { src: fall, alt: 'fall' }, 
        { src: summer, alt: 'sunny' }
      ] },
    { type: 'image', question: 'Choose the picture that matches your vibe the most:', images: [
        { src: la, alt: 'La landscape' }, 
        { src: dubai, alt: 'Dubai picture at night' },
        { src: soviet, alt: 'Soviet union depressing buldings' }, 
        { src: asia, alt: 'Asia village landscape' }
      ] },
    { type: 'image', question: 'Choose the picture that matches your vibe the most:', images: [
        { src: cat1, alt: 'Sleepy cat wrapped in blanket' }, 
        { src: cat2, alt: 'Confused cat during shower' },
        { src: cat3, alt: 'Proud cat that is dressed up as a flower' }, 
        { src: cat4, alt: 'Cat that looks like it is drunk' }
      ] },
    { type: 'rating', question: 'Rate this clip:', options: 
        ['ðŸ˜','ðŸ¤”','ðŸ’©'], 
        videoSrc: barnyard 
    },
    { type: 'rating', question: 'Rate this clip:', options: 
        ['ðŸ˜','ðŸ¤”','ðŸ’©'], 
        videoSrc: wolf 
    },
    { type: 'image', question: 'Who will be your companion?', images: [
        { src: donkey, alt: 'donkey from the shrek movies' },
        { src: michael, alt: 'michael scott from the office' },
        { src: bean, alt: 'mr. bean' },
        { src: ace, alt: 'ace ventura' }
      ] }
  ];

  const currentQuestion = questions[step];

  useEffect(() => {
    const fetchRecommendations = async () => {
      if (step === questions.length && feedback === null) {
        console.log("dupa")
        try {
          const requestBody = {
            questions_answers: answers.map((answer, index) => [
              questions[index + 1].question,
              answer
            ])
          };
          
          const response = await fetch('http://localhost:2334/', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json'
            },
            body: JSON.stringify(requestBody)
          });
  
          if (!response.ok) {
            throw new Error('Failed to fetch recommendations');
          }
          
          const textData = await response.text();
          // Parse the textData to extract movie recommendations
          const recommendationsRegex = /\((.+?), (\d{4}), (.+?), (.+?), (\d+\.\d+)\)/g;
          let match;
          const parsedRecommendations = [];
  
          while ((match = recommendationsRegex.exec(textData)) !== null) {
            const [, title, year, director, studio, imdbRating] = match;
            parsedRecommendations.push({
              title: title.trim(),
              director: director.trim(),
              year: year.trim(),
              studio: studio.trim(),
              imdbRating: parseFloat(imdbRating)
            });
          }
  
          setRecommendations(parsedRecommendations);
  
        } catch (error) {
          console.error('Error fetching recommendations:', error);
        }
      }
    };
  
    fetchRecommendations();
  }, [step, feedback, answers, questions]);
  

  return (
    <div className="quiz">
      <AnimatePresence>
        {step < questions.length ? (
          <motion.div
            key={step}
            initial={{ opacity: 0, y: 50 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -50 }}
            transition={{ duration: 0.3 }}
          >
            {currentQuestion.type === 'multi-choice' && (
              <Question
                question={currentQuestion.question}
                options={currentQuestion.options}
                onAnswer={handleAnswer}
              />
            )}
            {currentQuestion.type === 'image' && (
              <ImageSelection
                question={currentQuestion.question}
                images={currentQuestion.images}
                onAnswer={handleAnswer}
              />
            )}
            {currentQuestion.type === 'rating' && (
              <Rating
                question={currentQuestion.question}
                options={currentQuestion.options}
                videoSrc={currentQuestion.videoSrc}
                onAnswer={handleAnswer}
              />
            )}
          </motion.div>
        ) : feedback === null ? (
          recommendations ? (
            Array.isArray(recommendations) ? (
              recommendations.map((rec, index) => (
                <motion.div
                  key={`recommendation-${index}`}
                  initial={{ opacity: 0, y: 50 }}
                  animate={{ opacity: 1, y: 0 }}
                  exit={{ opacity: 0, y: -50 }}
                  transition={{ duration: 0.3 }}
                >
                  <Recommendation
                    movie={{
                      title: rec.title,
                      director: rec.director,
                      studio: rec.studio,
                      year: rec.year,
                      imdbRating: rec.imdbRating,
                      src: recommendation
                    }}
                    onFeedback={handleFeedback}
                  />
                </motion.div>
              ))
            ) : (
              <div>Error: Recommendations is not an array.</div>
            )
          ) : (
            <div>Loading recommendations...</div>
          )
        ) : (
          <motion.div
            key="thanks"
            initial={{ opacity: 0, y: 50 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -50 }}
            transition={{ duration: 0.3 }}
          >
            <FeedbackThanks />
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
}

export default Quiz;
